package android.example.tourguideapp.fragments

import android.content.Intent
import android.example.tourguideapp.R
import android.example.tourguideapp.databinding.FragmentYogaStudiosBinding
import android.net.Uri
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import com.bumptech.glide.Glide


class YogaStudiosFragment : Fragment() {
    private var _binding: FragmentYogaStudiosBinding? = null
    private val binding get() = _binding!!
    private val imageUrl = "data:image/jpeg;base64,"

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        _binding = FragmentYogaStudiosBinding.inflate(inflater, container, false)
        val view = binding.root

        Glide.with(this)
            .load(imageUrl)
            .into(binding.ivIconYogaStudio1)

        binding.tvAddressYogaStudio1.setOnClickListener {
            val intentUri = Uri.parse("google.streetview:cbll=42.85095259579171, 74.61088538013477")
            val mapIntent = Intent(Intent.ACTION_VIEW, intentUri )
            mapIntent.setPackage("com.google.android.apps.maps")
            startActivity(mapIntent)
        }
        return view


    }
}
